class Diglin_Username_Block_Adminhtml_Config_Source_Hint
    extends Mage_Adminhtml_Block_Abstract
    implements Varien_Data_Form_Element_Renderer_Interface
{
    /**
     * Render fieldset html
     *
     * @param Varien_Data_Form_Element_Abstract $element
     * @return string
     */
    public function render(Varien_Data_Form_Element_Abstract $element)
    {
        $version = Mage::getConfig()->getModuleConfig('Diglin_Username')->version;
        return '<p><a href="http://www.diglin.com/?utm_source=magento&utm_medium=extension&utm_campaign=username"><img style="vertical-align:middle" src="data:image/png;base64,"></a> <strong>Diglin_Username Version: ' . $version . '</strong></p>';
    }
}